import React, { useState } from 'react';
import './App.css';
import './Rooms.css'


export default function App() {



  const [rooms, setRoom] = useState([
    {},
    {},
    {},
    {},
    {},
    {},
  ])

  const deleteRoom = (evt) => {
    let tempRoom = rooms;

    tempRoom.splice(rooms.length-2);
    console.log(tempRoom)
    setRoom(tempRoom);
  }

  return (
    <div className="App">
      <RenderArrays rooms={rooms}/>
      <Buttons deleteRoom={deleteRoom}/>
    </div>
  );
}



function RenderArrays(props) {

  const renderRooms = () => {
    return props.rooms.map((room, n) => {
       return (<li className="room"> <div>{n + 1}</div> </li>);
    })
  }

  return (
    <ul className = "roomList">{renderRooms()}</ul>
  )
}

function Buttons(props) {
  return (
    <button className={deleteButton} onClick={props.deleteRoom}/>
  )
}
