import React, { useState } from 'react';
import './App.css';
import './Rooms.css'


export default class App extends React.Component{

  constructor(props) {
    super(props);
    this.state = {
      rooms: [
        {},
        {},
        {},
        {},
        {},
        {},
      ]
    };
  }


   deleteRoom = (evt) => {
    let tempRoom = this.state.rooms;
    tempRoom.splice(this.state.rooms.length-2);
    console.log(tempRoom)
    this.setState({
      rooms: tempRoom
    })
  }

  render() {
    return (
      <div className="App">
        <RenderArrays rooms={this.state.rooms}/>
        <Buttons deleteRoom={this.deleteRoom}/>
      </div>
    )
  }
}



function RenderArrays(props) {

  const renderRooms = () => {
    return props.rooms.map((room, n) => {
       return (<li className="room"> <div>{n + 1}</div> </li>);
    })
  }





  return (
    <ul className = "roomList">{renderRooms()}</ul>
  )
}

function Buttons(props) {
  return (
    <button className="deleteButton" onClick={props.deleteRoom}/>
  )
}
