import React, { useState } from 'react';
import './App.css';
import './Rooms.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'


export default class App extends React.Component{

  constructor(props) {
    super(props);
    this.state = {
      rooms: [{},{},{},{},{},{},],
      students: [{lastName:"toft", email: "ft32801@pausd.us"},{lastName:"toft", email: "ft32801@pausd.us"},{lastName:"toft", email: "ft32801@pausd.us"},],
    };
  }


   deleteRoom = (evt) => {
    let tempRoom = this.state.rooms;
    tempRoom.splice(this.state.rooms.length-1);
    console.log(tempRoom)
    this.setState({
      rooms: tempRoom
    })
  }

  createRoom = (evt) => {
    let tempRoom = this.state.rooms;
    tempRoom.push({});
    this.setState({
      rooms: tempRoom
    })
  }

  render() {
    return (
      <div className="App">
        <RenderArrays rooms={this.state.rooms}/>
        <Buttons rooms={this.state.rooms} deleteRoom={this.deleteRoom} createRoom={this.createRoom}/>

      </div>
    )
  }
}



function RenderArrays(props) {

  const renderRooms = () => {
    return props.rooms.map((room, n) => {
       return (<li className="room"> <div>{n + 1}</div> </li>);
    })
  }


  return (
    <ul className = "roomList">{renderRooms()}</ul>
  )
}

function Buttons(props) {
  return (
    <div>
      <div>There are: {props.rooms.length + 1} rooms </div>
      <button className="roomButton" onClick={props.deleteRoom}> - </button>
      <button className="roomButton" onClick={props.createRoom}> + </button>
    </div>
  )
}

function RenderStudents(props) {
  const studentRender = () => {
    return props.students.map((student, n) => {
       return (<li className="room"> <div>{student.lastName}</div> </li>);
    })
  }
  return(
    <ul>{studentRender()}</ul>
  )
 }
